refsect2.programlisting:
typedef struct {
  guint32 pixel;
  guint16 red;
  guint16 green;
  guint16 blue;
} GdkColor;

typedef struct {
  gint      size;
  GdkColor *colors;
} GdkColormap;

void                gdk_colormap_free_colors            (GdkColormap *colormap,
                                                         const GdkColor *colors,
                                                         gint n_colors);
