typedef struct {
  GdkColor fg[5];
  GdkColor bg[5];
  GdkColor light[5];
  GdkColor dark[5];
  GdkColor mid[5];
  GdkColor text[5];
  GdkColor base[5];
  GdkColor text_aa[5];		/* Halfway between text/base */

  GdkColor black;
  GdkColor white;
  PangoFontDescription *font_desc;

  gint xthickness;
  gint ythickness;

  GdkGC *fg_gc[5];
  GdkGC *bg_gc[5];
  GdkGC *light_gc[5];
  GdkGC *dark_gc[5];
  GdkGC *mid_gc[5];
  GdkGC *text_gc[5];
  GdkGC *base_gc[5];
  GdkGC *text_aa_gc[5];
  GdkGC *black_gc;
  GdkGC *white_gc;

  GdkPixmap *bg_pixmap[5];
} GtkStyle;

void                gtk_style_set_background            (GtkStyle *style,
                                                         GdkWindow *window,
                                                         GtkStateType state_type);

gboolean            gtk_style_lookup_color              (GtkStyle *style,
                                                         const gchar *color_name,
                                                         GdkColor *color);

typedef struct {
  gint left;
  gint right;
  gint top;
  gint bottom;
} GtkBorder;

typedef struct {
  /* quark-ified property identifier like "GtkScrollbar::spacing" */
  GQuark type_name;
  GQuark property_name;

  /* fields similar to GtkSettingsValue */
  gchar *origin;
  GValue value;
} GtkRcProperty;

gboolean            (*GtkRcPropertyParser)              (const GParamSpec *pspec,
                                                         const GString *rc_string,
                                                         GValue *property_value);
